// imports will always go here at the top
import ChatMsg from './components/ChatMessage.js';

const socket = io();

// utility functions for socket
function setUserID({ sID }) {
    // debugger;
    // save our unique ID generated by Socket on the server side - this is how we track individual connections to the chat service
    vm.socketID = sID;
}

function updatedMessage({ message }) {
    vm.messages.push(message);
}

function handleUserTyping(user){
  console.log('someone is typing...')
}

const { createApp } = Vue

const vm = createApp({
  data() {
    return {
      socketID: '',
      message: '',
      messages: [],
      nickname: ''
    }
  },
  
  methods: {
    dispatchMessage() {
        // debugger;
        socket.emit('chat_message', {
            content: this.message,
            name: this.nickname || 'anonymous',
            id: this.socketID
        })
        this.message = "";
    },

    detectTyping(){
      // emit a typing event and broadcast it to the chat
      socket.emit('user_is_typing', {
        name: this.nickname || 'anonymous'
      })
    }
  },
  
  components: {
    newmsg: ChatMsg

  }

}).mount('#app')

socket.addEventListener('connected', setUserID);
socket.addEventListener('message_update', updatedMessage);
socket.addEventListener('typing', handleUserTyping);